Wrangling <- Electricity_Coal %>% filter(Region %in% c('China','North America','India','Europe')) %>% mutate(El_Gen_in_Coal_Fraction = (El_Gen_Billion_Kilowatthours * 1000) / (1904*Coal_Reserve_Million_Tons),Remaining_Coal_Fraction = 1 - El_Gen_in_Coal_Fraction) %>% select(Region, El_Gen_in_Coal_Fraction, Remaining_Coal_Fraction) %>% gather(Type, Coal, El_Gen_in_Coal_Fraction:Remaining_Coal_Fraction); Wrangling